    _trendsProducts = trendsProducts;
    _popularProducts = popularProducts;
  }

  Value.fromJson(dynamic json) {
    _category =
        json['category'] != null ? Category.fromJson(json['category']) : null;
    if (json['banners'] != null) {
      _banners = [];
